const gulp   = require('gulp');
const minify = require('gulp-minify');

gulp.task('minify', function () {
    gulp.src('dist/wolk.js')
        .pipe(minify({
	        ext:{
	            src:'.min.js',
	            min:'.js'
	        }
    	}))
        .pipe(gulp.dest('dist'))
});

gulp.task('default', ['minify'], function() {

});